      const productUrl = `${siteUrl}/wp-json/wc/v3/products/${id}`;
      const productResponse = await fetch(productUrl, {
        headers: {
          'Authorization': `Basic ${auth}`,
          'Content-Type': 'application/json',
        },
      });

